import React, { useEffect, useState } from "react";
import { useAuth0 } from "@auth0/auth0-react";
import AnalysisResultCard from "./AnalysisResultCard";

function AnalysisHistory() {
  const [analyses, setAnalyses] = useState([]);
  const [expandedCard, setExpandedCard] = useState(null); // Track which card is expanded
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const { user } = useAuth0();

  useEffect(() => {
    async function fetchAnalyses() {
      setIsLoading(true);
      try {
        const userId = encodeURIComponent(user.sub);
        const res = await fetch(`http://localhost:4000/analyses?userId=${userId}`);
        if (!res.ok) {
          throw new Error(`Server responded with status ${res.status}`);
        }
        const data = await res.json();
        setAnalyses(data.analyses);
      } catch (err) {
        console.error(err);
        setError("Error fetching analyses.");
      } finally {
        setIsLoading(false);
      }
    }
    if (user) {
      fetchAnalyses();
    }
  }, [user]);

  return (
    <div
      className="min-h-screen pt-24 pb-12"
      style={{ background: "linear-gradient(to bottom, #f5fff5, #d5f5e3)" }}
    >
      {/* Page Heading */}
      <h1 className="text-4xl font-extrabold text-center text-green-700 mb-10">
        My Resume Analyses
      </h1>

      {isLoading && (
        <p className="text-center text-lg font-semibold">Loading analyses...</p>
      )}
      {error && <p className="text-center text-red-500">{error}</p>}
      {!isLoading && analyses.length === 0 && (
        <p className="text-center text-lg text-gray-600">No analyses found.</p>
      )}

      {/* Analysis Cards (Using AnalysisResultCard.js) */}
      {analyses.length > 0 && (
        <div className="container mx-auto px-6 md:px-12 lg:px-16 max-w-5xl">
          <ul className="space-y-4">
            {analyses.map((analysis) => (
              <li key={analysis._id} className="cursor-pointer">
                <div
                  className={`transition-all duration-300 ease-in-out transform ${
                    expandedCard === analysis._id
                      ? "scale-105 shadow-2xl"
                      : "scale-95"
                  }`}
                  onClick={() =>
                    setExpandedCard(expandedCard === analysis._id ? null : analysis._id)
                  }
                  onMouseEnter={() => setExpandedCard(analysis._id)}
                  onMouseLeave={() => setExpandedCard(null)}
                >
                  <AnalysisResultCard result={analysis} />
                </div>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default AnalysisHistory;
