import React, { useEffect, useState } from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import AnalysisResultCard from './AnalysisResultCard';
import { X } from 'lucide-react';

function AnalysisHistory() {
  const [analyses, setAnalyses] = useState([]);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const { user } = useAuth0();
  const [expandedCard, setExpandedCard] = useState(null);

  useEffect(() => {
    async function fetchAnalyses() {
      setIsLoading(true);
      try {
        const userId = encodeURIComponent(user.sub);
        const res = await fetch(`http://localhost:4000/analyses?userId=${userId}`);
        if (!res.ok) {
          throw new Error(`Server responded with status ${res.status}`);
        }
        const data = await res.json();

        // Ensure `missingKeywords` and `suggestions` are valid
        const formattedData = data.analyses.map(analysis => ({
          ...analysis,
          missingKeywords: Array.isArray(analysis.missingKeywords) ? analysis.missingKeywords : [],
          suggestions: analysis.suggestions || "No suggestions provided."
        }));

        setAnalyses(formattedData);
      } catch (err) {
        console.error(err);
        setError('Error fetching analyses.');
      } finally {
        setIsLoading(false);
      }
    }
    if (user) {
      fetchAnalyses();
    }
  }, [user]);

  return (
    <div className="container mx-auto mt-12 p-8 bg-gray-50">
      <h1 className="text-3xl font-semibold text-green-800 mb-6 text-center">My Resume Analyses</h1>

      {isLoading && <p className="text-center text-gray-600">Loading analyses...</p>}
      {error && <p className="text-center text-red-500">{error}</p>}
      {!isLoading && analyses.length === 0 && <p className="text-center text-gray-600">No analyses found.</p>}

      {/* Grid layout for small cards */}
      <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-4">
        {analyses.map((analysis) => (
          <div 
            key={analysis._id} 
            className="bg-white shadow-md rounded-xl p-4 text-center cursor-pointer transition-transform transform hover:scale-105"
            onClick={() => setExpandedCard(analysis)}
          >
            <p className="text-lg font-bold text-green-700">ATS: {analysis.atsScore}</p>
            <p className="text-sm text-gray-600">
              {analysis.missingKeywords.length > 0 
                ? `Missing: ${analysis.missingKeywords.slice(0, 2).join(', ')}...`
                : "No missing keywords"}
            </p>
          </div>
        ))}
      </div>

      {/* Expanded View on Click (Full Screen Mode) */}
      {expandedCard && (
        <div className="fixed inset-0 bg-white shadow-2xl z-50 flex justify-center items-center">
          <div className="relative w-full max-w-3xl max-h-screen overflow-y-auto p-6 rounded-2xl shadow-lg border border-green-400">
            
            {/* Close Button (Always Fixed at the Top) */}
            <button 
              className="absolute top-4 right-6 text-gray-700 hover:text-red-600 text-2xl"
              onClick={() => setExpandedCard(null)}
            >
              <X size={30} />
            </button>

            {/* Full Analysis Card */}
            <AnalysisResultCard result={expandedCard} />
          </div>
        </div>
      )}
    </div>
  );
}

export default AnalysisHistory;
