import React from 'react';
import { Link } from 'react-router-dom';
import { AlignHorizontalJustifyCenter, Upload, BarChart2, Power } from 'lucide-react';
import { useAuth0 } from '@auth0/auth0-react';

function Navbar() {
  const { isAuthenticated, logout } = useAuth0();

  return (
    <nav className="p-4 shadow-md" style={{ backgroundColor: '#58d68d' }}>
      <div className="container mx-auto flex items-center justify-between">
        {/* Logo / Title */}
        <Link to="/" className="flex items-center text-xl font-bold" style={{ color: '#000000' }}>
          <AlignHorizontalJustifyCenter className="mr-2" size={36} />
          Alignify
        </Link>

        {/* Nav Links */}
        <div className="flex items-center space-x-6">
          <Link to="/resume-analysis" className="flex items-center font-semibold transition-colors" style={{ color: '#000000' }}>
            <Upload className="mr-1" size={20} />
            ATS Scanner
          </Link>

          {isAuthenticated && (
            <>
              <Link to="/dashboard" className="flex items-center font-semibold transition-colors" style={{ color: '#000000' }}>
                <BarChart2 className="mr-1" size={20} />
                Dashboard
              </Link>
              <button
                onClick={() => logout({ returnTo: window.location.origin + '/login' })}
                className="flex items-center font-bold transition-colors hover:text-red-600"
                style={{ color: '#000000' }}
              >
                <Power className="mr-1" size={20} />
                Logout
              </button>
            </>
          )}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
