import React from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import { LogIn, Upload, BarChart3, ArrowDown } from 'lucide-react';

const LoginPage = () => {
  const { loginWithRedirect } = useAuth0();

  return (
    <div className="flex items-center justify-center min-h-screen bg-white">
      <div className="flex items-center justify-center space-x-16 max-w-5xl w-full px-6">
        {/* Login Card - Slightly Left */}
        <div className="p-10 bg-green-100 rounded-2xl shadow-xl text-center transition-transform transform hover:scale-105 duration-300 w-1/2">
          <h1 className="text-3xl font-bold mb-6 text-green-700">Welcome to Alignify</h1>
          <p className="text-gray-700 mb-6">Log in to unlock the full potential of your resume.</p>
          <button
            onClick={() => loginWithRedirect()}
            className="px-6 py-3 bg-green-700 text-white font-semibold rounded-lg transition-all hover:bg-green-800 hover:shadow-lg"
          >
            Log In
          </button>
        </div>

        {/* How It Works - Flowchart Style */}
        <div className="w-1/2 flex flex-col items-center">
          <h2 className="text-xl font-bold text-green-700 mb-6">How It Works</h2>
          
          {/* Step 1 */}
          <div className="flex flex-col items-center bg-green-200 rounded-xl p-4 shadow-md hover:shadow-lg transition-transform transform hover:scale-105 w-64">
            <LogIn className="text-green-700 w-10 h-10 mb-2" />
            <h3 className="font-semibold text-green-800">1. Login</h3>
            <p className="text-gray-600 text-sm text-center">Sign in securely to access your dashboard.</p>
          </div>

          {/* Arrow */}
          <ArrowDown className="text-green-500 my-2 w-6 h-6" />

          {/* Step 2 */}
          <div className="flex flex-col items-center bg-green-200 rounded-xl p-4 shadow-md hover:shadow-lg transition-transform transform hover:scale-105 w-64">
            <Upload className="text-green-700 w-10 h-10 mb-2" />
            <h3 className="font-semibold text-green-800">2. Upload</h3>
            <p className="text-gray-600 text-sm text-center">Submit your resume for AI analysis.</p>
          </div>

          {/* Arrow */}
          <ArrowDown className="text-green-500 my-2 w-6 h-6" />

          {/* Step 3 */}
          <div className="flex flex-col items-center bg-green-200 rounded-xl p-4 shadow-md hover:shadow-lg transition-transform transform hover:scale-105 w-64">
            <BarChart3 className="text-green-700 w-10 h-10 mb-2" />
            <h3 className="font-semibold text-green-800">3. Improve</h3>
            <p className="text-gray-600 text-sm text-center">Get actionable insights to optimize your resume.</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
