// src/components/AnalysisHistory.js
import React, { useEffect, useState } from "react";
import { useAuth0 } from "@auth0/auth0-react";
import AnalysisResultCard from "./AnalysisResultCard";

function AnalysisHistory() {
  const [analyses, setAnalyses] = useState([]);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const { user } = useAuth0();

  useEffect(() => {
    async function fetchAnalyses() {
      setIsLoading(true);
      try {
        if (!user?.sub) {
          throw new Error("User not authenticated.");
        }

        const userId = encodeURIComponent(user.sub);
        const res = await fetch(`http://localhost:4000/analyses?userId=${userId}`);

        if (!res.ok) {
          throw new Error(`Server responded with status ${res.status}`);
        }

        const data = await res.json();
        console.log("Fetched Analyses:", data.analyses); // Debugging

        if (!Array.isArray(data.analyses)) {
          throw new Error("Invalid response format.");
        }

        // Ensure missingKeywords exists
        const formattedAnalyses = data.analyses.map((analysis) => ({
          ...analysis,
          missingKeywords: analysis.missingKeywords || [],
        }));

        setAnalyses(formattedAnalyses);
      } catch (err) {
        console.error("Error fetching analyses:", err);
        setError("Error fetching analyses.");
      } finally {
        setIsLoading(false);
      }
    }

    if (user) {
      fetchAnalyses();
    }
  }, [user]);

  return (
    <div className="flex flex-col items-center min-h-screen pt-20 pb-12 bg-green-50">
      <h1 className="text-4xl font-bold mb-6 text-center text-green-800">
        My Resume Analyses
      </h1>

      {isLoading && <p className="text-green-700">Loading analyses...</p>}
      {error && <p className="text-red-500">{error}</p>}

      {/* Ensure analyses is not empty before rendering */}
      {!isLoading && analyses.length === 0 && <p className="text-gray-600">No analyses found.</p>}

      <div className="grid grid-cols-2 md:grid-cols-3 gap-6 w-full max-w-6xl px-6">
        {analyses.map((analysis) => (
          <div
            key={analysis._id}
            className="transform transition-all duration-300 hover:scale-105"
          >
            <AnalysisResultCard analysis={analysis} />
          </div>
        ))}
      </div>
    </div>
  );
}

export default AnalysisHistory;
