// src/components/AnalysisHistory.js
import React, { useEffect, useState } from 'react';
import { useAuth0 } from '@auth0/auth0-react';

function AnalysisHistory() {
  const [analyses, setAnalyses] = useState([]);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const { user } = useAuth0();

  useEffect(() => {
    async function fetchAnalyses() {
      setIsLoading(true);
      try {
        const userId = encodeURIComponent(user.sub);
        const res = await fetch(`http://localhost:4000/analyses?userId=${userId}`);
        if (!res.ok) {
          throw new Error(`Server responded with status ${res.status}`);
        }
        const data = await res.json();
        setAnalyses(data.analyses);
      } catch (err) {
        console.error(err);
        setError('Error fetching analyses.');
      } finally {
        setIsLoading(false);
      }
    }
    if (user) {
      fetchAnalyses();
    }
  }, [user]);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center pt-24 pb-16"
         style={{ background: 'linear-gradient(to bottom, #f5fff5, #d5f5e3)' }}>
      <h1 className="text-4xl font-bold text-green-800 mb-8">My Resume Analyses</h1>

      {isLoading && <p className="text-lg text-gray-700">Loading analyses...</p>}
      {error && <p className="text-red-500 font-semibold">{error}</p>}
      {!isLoading && analyses.length === 0 && <p className="text-gray-700 text-lg">No analyses found.</p>}
      
      {analyses.length > 0 && (
        <div className="w-full max-w-5xl space-y-6">
          {analyses.map((analysis) => (
            <div key={analysis._id} className="p-6 bg-white/90 backdrop-blur-lg shadow-2xl rounded-3xl transition-transform transform hover:scale-105 hover:shadow-2xl duration-300">
              <p className="text-lg font-semibold text-gray-800">
                <strong>Job Description:</strong> {analysis.jobDescription}
              </p>
              <p className="text-lg font-semibold text-green-700">
                <strong>ATS Score:</strong> {analysis.atsScore}
              </p>
              <p className="text-lg font-semibold text-gray-700">
                <strong>Missing Keywords:</strong>{' '}
                {analysis.missingKeywords && analysis.missingKeywords.length > 0
                  ? analysis.missingKeywords.join(', ')
                  : 'None'}
              </p>
              <p className="text-lg font-semibold text-gray-800">
                <strong>Suggestions:</strong>
              </p>
              <ul className="list-disc list-inside text-gray-600 mt-2">
                {analysis.suggestions.split('\n').map((suggestion, index) => (
                  <li key={index} className="mb-2">{suggestion}</li>
                ))}
              </ul>
              <p className="text-sm text-gray-500 mt-4">
                Analyzed on: {new Date(analysis.createdAt).toLocaleString()}
              </p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default AnalysisHistory;
