import React, { useEffect, useState } from 'react';
import { useAuth0 } from '@auth0/auth0-react';

function AnalysisHistory() {
  const [analyses, setAnalyses] = useState([]);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const { user } = useAuth0();

  useEffect(() => {
    async function fetchAnalyses() {
      setIsLoading(true);
      try {
        const userId = encodeURIComponent(user.sub);
        const res = await fetch(`http://localhost:4000/analyses?userId=${userId}`);
        if (!res.ok) {
          throw new Error(`Server responded with status ${res.status}`);
        }
        const data = await res.json();
        setAnalyses(data.analyses);
      } catch (err) {
        console.error(err);
        setError('Error fetching analyses.');
      } finally {
        setIsLoading(false);
      }
    }
    if (user) {
      fetchAnalyses();
    }
  }, [user]);

  return (
    <div className="flex flex-col items-center min-h-screen py-12" 
         style={{ background: 'linear-gradient(to bottom, #f5fff5, #d5f5e3)' }}>
      
      <h1 className="text-4xl font-bold text-green-800 mb-8">My Resume Analyses</h1>

      {isLoading && <p className="text-lg text-gray-600">Loading analyses...</p>}
      {error && <p className="text-red-500 text-lg">{error}</p>}
      {!isLoading && analyses.length === 0 && <p className="text-lg text-gray-600">No analyses found.</p>}

      {analyses.length > 0 && (
        <ul className="w-full max-w-4xl space-y-6">
          {analyses.map((analysis) => (
            <li key={analysis._id} className="p-6 bg-white rounded-3xl shadow-lg transition-transform transform hover:scale-105 duration-300">
              <p className="text-lg font-semibold text-green-700">
                <strong>Job Description:</strong> {analysis.jobDescription}
              </p>
              <p className="text-lg font-semibold text-gray-700">
                <strong>ATS Score:</strong> {analysis.atsScore}
              </p>
              
              {/* Missing Keywords */}
              <div className="mb-3">
                <span className="font-semibold text-green-700">Missing Keywords:</span>
                <ul className="list-disc pl-5 mt-1 text-gray-800 space-y-1">
                  {analysis.missingKeywords.length > 0
                    ? analysis.missingKeywords.map((keyword, index) => <li key={index}>{keyword}</li>)
                    : <li>None</li>}
                </ul>
              </div>

              {/* Suggestions - Proper bullet format */}
              <div className="mb-3">
                <span className="font-semibold text-green-700">Suggestions:</span>
                <ul className="list-disc pl-5 mt-1 text-gray-800 space-y-2">
                  {Array.isArray(analysis.suggestions)
                    ? analysis.suggestions.map((suggestion, index) => <li key={index}>{suggestion}</li>)
                    : <li>{analysis.suggestions}</li>}
                </ul>
              </div>

              <p className="text-sm text-gray-500 mt-2">
                Analyzed on: {new Date(analysis.createdAt).toLocaleString()}
              </p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default AnalysisHistory;
