import React from 'react';
import { useAuth0 } from '@auth0/auth0-react';

const LoginPage = () => {
  const { loginWithRedirect } = useAuth0();

  return (
    <div className="flex items-center justify-center min-h-screen bg-green-500">
      <div className="p-10 bg-white rounded-2xl shadow-xl text-center transition-transform transform hover:scale-105 duration-300">
        <h1 className="text-3xl font-bold mb-6 text-green-700">Welcome to Alignify</h1>
        <p className="text-gray-600 mb-6">Log in to unlock the full potential of your resume.</p>
        <button
          onClick={() => loginWithRedirect()}
          className="px-6 py-3 bg-green-700 text-white font-semibold rounded-lg transition-all hover:bg-green-800 hover:shadow-lg"
        >
          Log In
        </button>
      </div>
    </div>
  );
};

export default LoginPage;
