import React, { useState } from 'react';
import { X } from 'lucide-react';

function ResumeAnalysisPage() {
  const [resumeFile, setResumeFile] = useState(null);
  const [jobDescription, setJobDescription] = useState('');
  const [analysisResult, setAnalysisResult] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    setResumeFile(file);
  };

  const handleAnalysis = async () => {
    if (!resumeFile) {
      setError('Please upload a resume file.');
      return;
    }
    if (!jobDescription) {
      setError('Please enter a job description.');
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      // Prepare FormData with the file and job description
      const formData = new FormData();
      formData.append('resumeFile', resumeFile);
      formData.append('jobDescription', jobDescription);

      // Send FormData to the backend
      const res = await fetch('http://localhost:4000/chat', {
        method: 'POST',
        body: formData
      });

      if (!res.ok) {
        throw new Error(`Server responded with status ${res.status}`);
      }

      const data = await res.json();
      setAnalysisResult(data.response);
    } catch (err) {
      console.error("Error fetching response:", err);
      setError("Error fetching response.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="container mx-auto mt-12 p-8 bg-white shadow-lg rounded-lg">
      <h1 className="text-2xl font-semibold text-gray-800 mb-6">
        Resume Analysis üìù
      </h1>

      {/* Resume File Upload */}
      <div className="mb-4">
        <label
          htmlFor="resumeUpload"
          className="block text-gray-700 text-sm font-bold mb-2"
        >
          Upload Resume:
        </label>
        <input
          type="file"
          id="resumeUpload"
          onChange={handleFileChange}
          className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
        />
      </div>

      {/* Job Description Input */}
      <div className="mb-4">
        <label
          htmlFor="jobDescription"
          className="block text-gray-700 text-sm font-bold mb-2"
        >
          Job Description:
        </label>
        <textarea
          id="jobDescription"
          rows="4"
          placeholder="Enter the job description..."
          value={jobDescription}
          onChange={(e) => setJobDescription(e.target.value)}
          className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
        />
      </div>

      <button
        onClick={handleAnalysis}
        className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
        disabled={isLoading}
      >
        {isLoading ? 'Analyzing...' : 'Analyze Resume'}
      </button>

      {error && (
        <div className="mt-4 text-red-500">
          <X className="inline-block mr-1" size={16} />
          {error}
        </div>
      )}

      {/* Render Analysis Result */}
      {analysisResult && typeof analysisResult === 'object' && (
        <div className="mt-8">
          <h2 className="text-xl font-semibold text-gray-700 mb-4">
            Analysis Result:
          </h2>
          <div className="bg-gray-100 p-4 rounded-lg space-y-4">
            {/* Final Score */}
            <div>
              <span className="font-bold">Final Score: </span>
              <span>{analysisResult.finalScore ?? 'N/A'}</span>
            </div>

            {/* ATS Score */}
            <div>
              <span className="font-bold">ATS Score: </span>
              <span>{analysisResult.atsScore ?? 'N/A'}</span>
            </div>

            {/* Missing Keywords */}
            <div>
              <span className="font-bold">Missing Keywords:</span>
              {analysisResult.missingKeywords && analysisResult.missingKeywords.length > 0 ? (
                <ul className="list-disc list-inside ml-4 mt-1">
                  {analysisResult.missingKeywords.map((keyword, index) => (
                    <li key={index}>{keyword}</li>
                  ))}
                </ul>
              ) : (
                <p className="ml-2">None</p>
              )}
            </div>

            {/* Suggestions */}
            <div>
              <span className="font-bold">Suggestions:</span>
              <p className="mt-1 whitespace-pre-wrap">
                {analysisResult.suggestions || 'No suggestions provided.'}
              </p>
            </div>
          </div>
        </div>
      )}

      {/* Fallback if analysisResult is just a string */}
      {analysisResult && typeof analysisResult === 'string' && (
        <div className="mt-8 bg-gray-100 p-4 rounded-lg">
          <pre className="text-gray-600 whitespace-pre-wrap">
            {analysisResult}
          </pre>
        </div>
      )}
    </div>
  );
}

export default ResumeAnalysisPage;
