import React from 'react';
import { Link } from 'react-router-dom';
import { ArrowRight } from 'lucide-react';

function HomePage() {
  return (
    <div className="min-h-screen bg-gray-50 pt-40 pb-20"> {/* pt-20 to prevent navbar overlap */}
      <div className="container mx-auto px-6 md:px-12 lg:px-16 max-w-4xl">
        {/* Main Heading */}
        <h1 className="text-4xl font-bold mb-6 text-center text-black">
          Unlock Your Career with <span className="text-green-700">Alignify</span>
        </h1>

        {/* Subtext */}
        <p className="leading-relaxed mb-10 text-center text-gray-700 text-lg">
          Transform your resume into your most powerful career asset. Our AI-powered ATS Resume Reviewer 
          delivers personalized, real-time feedback that not only boosts your ATS score but also fine-tunes 
          your resume for human recruiters. Whether you're just starting out or looking to climb the career 
          ladder, Alignify is your partner in achieving your dream job.
        </p>

        {/* Card Container */}
        <div className="flex flex-col items-center gap-6">
          {/* Resume Analysis Card */}
          <div className="p-6 w-full max-w-3xl bg-green-100 rounded-2xl shadow-md transition-transform transform hover:scale-105 hover:shadow-2xl duration-300">
            <h2 className="text-2xl font-semibold mb-3 text-green-800">Alignify Your Resume</h2>
            <p className="text-gray-800">
              Upload your resume and let Alignify's intelligent AI unlock its full potential. Our system performs 
              a deep dive into your document, evaluating ATS compatibility, refining keyword strategies, and 
              assessing overall impact. Get clear, actionable insights that help you stand out in every job application.
            </p>
            <Link to="/resume-analysis" className="mt-4 inline-block px-5 py-3 bg-green-700 text-white font-semibold rounded-lg transition-all hover:bg-green-800 hover:shadow-lg">
              Get Started <ArrowRight className="inline ml-2" size={16} />
            </Link>
          </div>

          {/* Dashboard Access Card */}
          <div className="p-6 w-full max-w-3xl bg-green-100 rounded-2xl shadow-md transition-transform transform hover:scale-105 hover:shadow-2xl duration-300">
            <h2 className="text-2xl font-semibold mb-3 text-green-800">Go to Your Dashboard</h2>
            <p className="text-gray-800">
              Access your dashboard to review past scans, get recommendations, and fine-tune your resume based 
              on AI-driven insights.
            </p>
            <Link to="/dashboard" className="mt-4 inline-block px-5 py-3 bg-green-700 text-white font-semibold rounded-lg transition-all hover:bg-green-800 hover:shadow-lg">
              Go to Dashboard <ArrowRight className="inline ml-2" size={16} />
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
}

export default HomePage;
