import React from 'react';
import { Link } from 'react-router-dom';
import { AlignHorizontalJustifyCenter, Upload, BarChart2, Power } from 'lucide-react';
import { useAuth0 } from '@auth0/auth0-react';

function Navbar() {
  const { isAuthenticated, logout } = useAuth0();

  return (
    <nav className="fixed top-0 w-full bg-green-500 p-4 shadow-md z-50">
      <div className="container mx-auto flex items-center justify-between">
        {/* Logo / Title */}
        <Link to="/" className="flex items-center text-xl font-bold transition-transform transform hover:scale-105 duration-300 text-white">
          <AlignHorizontalJustifyCenter className="mr-2" size={36} />
          Alignify
        </Link>

        {/* Nav Links */}
        <div className="flex items-center space-x-6">
          <Link to="/resume-analysis" className="flex items-center font-semibold px-3 py-2 rounded-lg transition-all transform hover:scale-105 hover:bg-green-600 duration-300 ease-in-out text-white">
            <Upload className="mr-1" size={20} />
            ATS Scanner
          </Link>

          {isAuthenticated && (
            <>
              <Link to="/dashboard" className="flex items-center font-semibold px-3 py-2 rounded-lg transition-all transform hover:scale-105 hover:bg-green-600 duration-300 ease-in-out text-white">
                <BarChart2 className="mr-1" size={20} />
                Dashboard
              </Link>
              <button
                onClick={() => logout({ returnTo: window.location.origin + '/login' })}
                className="flex items-center font-bold px-3 py-2 rounded-lg transition-all transform hover:scale-105 hover:bg-red-600 duration-300 ease-in-out text-white"
              >
                <Power className="mr-1" size={20} />
                Logout
              </button>
            </>
          )}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
