import React, { useEffect, useState } from "react";
import { useAuth0 } from "@auth0/auth0-react";

function AnalysisHistory() {
  const [analyses, setAnalyses] = useState([]);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const { user } = useAuth0();

  useEffect(() => {
    async function fetchAnalyses() {
      setIsLoading(true);
      try {
        const userId = encodeURIComponent(user.sub);
        const res = await fetch(`http://localhost:4000/analyses?userId=${userId}`);
        if (!res.ok) {
          throw new Error(`Server responded with status ${res.status}`);
        }
        const data = await res.json();
        setAnalyses(data.analyses);
      } catch (err) {
        console.error(err);
        setError("Error fetching analyses.");
      } finally {
        setIsLoading(false);
      }
    }
    if (user) {
      fetchAnalyses();
    }
  }, [user]);

  return (
    <div className="min-h-screen pt-24 pb-12"
      style={{ background: "linear-gradient(to bottom, #f5fff5, #d5f5e3)" }}>

      {/* Page Heading */}
      <h1 className="text-4xl font-extrabold text-center text-green-700 mb-10">
        My Resume Analyses
      </h1>

      {isLoading && <p className="text-center text-lg font-semibold">Loading analyses...</p>}
      {error && <p className="text-center text-red-500">{error}</p>}
      {!isLoading && analyses.length === 0 && <p className="text-center text-lg text-gray-600">No analyses found.</p>}

      {/* Analysis Cards */}
      {analyses.length > 0 && (
        <div className="container mx-auto px-6 md:px-12 lg:px-16 max-w-5xl">
          <ul className="space-y-6">
            {analyses.map((analysis) => (
              <li key={analysis._id} className="bg-white/80 backdrop-blur-lg p-8 rounded-2xl shadow-xl transition-transform transform hover:scale-105 hover:shadow-2xl duration-300">
                
                {/* Job Description */}
                <p className="text-xl font-semibold text-green-800 mb-2">
                  Job Description:
                  <span className="text-gray-800 font-normal"> {analysis.jobDescription}</span>
                </p>

                {/* ATS Score */}
                <p className="text-lg font-semibold text-green-700">
                  ATS Score:
                  <span className="text-gray-800 font-normal"> {analysis.atsScore}</span>
                </p>

                {/* Missing Keywords */}
                <div className="mb-4">
                  <p className="text-lg font-semibold text-green-700">Missing Keywords:</p>
                  {analysis.missingKeywords && analysis.missingKeywords.length > 0 ? (
                    <ul className="list-disc list-inside text-gray-700">
                      {analysis.missingKeywords.map((keyword, index) => (
                        <li key={index}>{keyword}</li>
                      ))}
                    </ul>
                  ) : (
                    <p className="text-gray-700 ml-2">None</p>
                  )}
                </div>

                {/* Suggestions */}
                <div>
                  <p className="text-lg font-semibold text-green-700">Suggestions:</p>
                  <p className="text-gray-700 whitespace-pre-line mt-2">{analysis.suggestions}</p>
                </div>

                {/* Timestamp */}
                <p className="text-sm text-gray-500 mt-4">
                  Analyzed on: {new Date(analysis.createdAt).toLocaleString()}
                </p>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default AnalysisHistory;
