import React from 'react';

function AnalysisResultCard({ analysis }) {
  return (
    <div style={{ border: '1px solid #ccc', margin: '1rem', padding: '1rem' }}>
      <h3>ATS Score: {analysis.atsScore}</h3>

      {/* Render missingKeywords in the same line as the heading */}
      <h4>
        Missing Keywords / Areas:{" "}
        <span>
          {analysis.missingKeywords && analysis.missingKeywords.length > 0
            ? analysis.missingKeywords.join(', ')
            : 'None'}
        </span>
      </h4>

      {/* Render suggestions as separate paragraphs */}
      <h4>Suggestions:</h4>
      {analysis.suggestions && analysis.suggestions.map((suggestion, i) => (
        <p key={i}>{suggestion}</p>
      ))}
    </div>
  );
}

export default AnalysisResultCard;
