// src/components/ResumeAnalysisPage.js
import React, { useState } from 'react';
import { Upload, X } from 'lucide-react';

function ResumeAnalysisPage() {
  const [resumeFile, setResumeFile] = useState(null);
  const [jobDescription, setJobDescription] = useState('');
  const [analysisResult, setAnalysisResult] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    setResumeFile(file);
  };

  const handleAnalysis = async () => {
    if (!resumeFile) {
      setError('Please upload a resume file.');
      return;
    }
    if (!jobDescription) {
      setError('Please enter a job description.');
      return;
    }

    setIsLoading(true);
    setError(null);

    // Read the resume file content using FileReader
    const reader = new FileReader();
    reader.onload = async (e) => {
      const resumeText = e.target.result;

      try {
        // Send resumeText and jobDescription to the backend /chat endpoint
        const res = await fetch('http://localhost:4000/chat', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            resumeText: resumeText,
            jobDescription: jobDescription,
          }),
        });
        const data = await res.json();
        setAnalysisResult(data.response);
      } catch (err) {
        console.error("Error fetching response:", err);
        setError("Error fetching response.");
      } finally {
        setIsLoading(false);
      }
    };

    reader.onerror = () => {
      setError("Error reading the resume file.");
      setIsLoading(false);
    };

    reader.readAsText(resumeFile);
  };

  return (
    <div className="container mx-auto mt-12 p-8 bg-white shadow-lg rounded-lg">
      <h1 className="text-2xl font-semibold text-gray-800 mb-6">Resume Analysis üìù</h1>

      {/* Resume File Upload */}
      <div className="mb-4">
        <label htmlFor="resumeUpload" className="block text-gray-700 text-sm font-bold mb-2">
          Upload Resume:
        </label>
        <input
          type="file"
          id="resumeUpload"
          onChange={handleFileChange}
          className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
        />
      </div>

      {/* Job Description Input */}
      <div className="mb-4">
        <label htmlFor="jobDescription" className="block text-gray-700 text-sm font-bold mb-2">
          Job Description:
        </label>
        <textarea
          id="jobDescription"
          rows="4"
          placeholder="Enter the job description..."
          value={jobDescription}
          onChange={(e) => setJobDescription(e.target.value)}
          className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
        />
      </div>

      <button
        onClick={handleAnalysis}
        className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
        disabled={isLoading}
      >
        {isLoading ? 'Analyzing...' : 'Analyze Resume'}
      </button>

      {error && (
        <div className="mt-4 text-red-500">
          <X className="inline-block mr-1" size={16} />
          {error}
        </div>
      )}

      {analysisResult && (
        <div className="mt-8">
          <h2 className="text-xl font-semibold text-gray-700 mb-4">Analysis Result:</h2>
          <div className="bg-gray-100 p-4 rounded-lg">
            <pre className="text-gray-600 whitespace-pre-wrap">
              {typeof analysisResult === 'object'
                ? JSON.stringify(analysisResult, null, 2)
                : analysisResult}
            </pre>
          </div>
        </div>
      )}
    </div>
  );
}

export default ResumeAnalysisPage;
