import React, { useEffect, useState } from "react";
import { useAuth0 } from "@auth0/auth0-react";
import AnalysisResultCard from "./AnalysisResultCard";

function AnalysisHistory() {
  const [analyses, setAnalyses] = useState([]);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const { user } = useAuth0();

  useEffect(() => {
    async function fetchAnalyses() {
      setIsLoading(true);
      try {
        const userId = encodeURIComponent(user.sub);
        const res = await fetch(`http://localhost:4000/analyses?userId=${userId}`);
        if (!res.ok) {
          throw new Error(`Server responded with status ${res.status}`);
        }
        const data = await res.json();
        setAnalyses(data.analyses);
      } catch (err) {
        console.error(err);
        setError("Error fetching analyses.");
      } finally {
        setIsLoading(false);
      }
    }
    if (user) {
      fetchAnalyses();
    }
  }, [user]);

  return (
    <div className="min-h-screen pt-24 pb-12"
      style={{ background: "linear-gradient(to bottom, #f5fff5, #d5f5e3)" }}>
      
      {/* Page Heading */}
      <h1 className="text-4xl font-extrabold text-center text-green-700 mb-10">
        My Resume Analyses
      </h1>

      {isLoading && (
        <p className="text-center text-lg font-semibold">Loading analyses...</p>
      )}
      {error && <p className="text-center text-red-500">{error}</p>}
      {!isLoading && analyses.length === 0 && (
        <p className="text-center text-lg text-gray-600">No analyses found.</p>
      )}

      {/* Analysis Cards Grid */}
      {analyses.length > 0 && (
        <div className="container mx-auto px-6 md:px-12 lg:px-16 max-w-6xl">
          <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-5 gap-4">
            {analyses.map((analysis) => (
              <div
                key={analysis._id}
                className="transition-all duration-300 ease-in-out transform hover:scale-105 
                  hover:shadow-2xl cursor-pointer overflow-hidden relative"
                style={{
                  width: "120px", 
                  height: "120px",
                  background: "rgba(255, 255, 255, 0.9)", 
                  padding: "10px",
                  borderRadius: "12px",
                  boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
                  transition: "all 0.3s ease-in-out"
                }}
              >
                {/* Small View - Default */}
                <div className="text-center">
                  <h3 className="text-xl font-semibold text-green-700">ATS: {analysis.atsScore}</h3>
                  <p className="text-sm text-gray-600 mt-1">
                    {analysis.missingKeywords.length > 0
                      ? `Missing: ${analysis.missingKeywords.slice(0, 2).join(", ")}...`
                      : "No missing keywords"}
                  </p>
                </div>

                {/* Expanded View on Hover */}
                <div className="absolute top-0 left-0 w-full h-full opacity-0 hover:opacity-100 
                                transition-all duration-300 ease-in-out bg-white p-4 rounded-lg shadow-lg">
                  <AnalysisResultCard result={analysis} />
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default AnalysisHistory;
